import React, { useEffect } from 'react';
import Delay from 'react-delay';
import '../../animation';

import { TimelineLite, Elastic } from 'gsap';

import styled, { keyframes } from 'styled-components';

import Animations from '../sections/Animations';

const StyledHeroSection = styled.section`
	display: grid;
	grid-template-columns: repeat(2, auto)
	align-items: center;
	justify-content: center;
	height: 100vh;
	padding: 30px 30px 30px 30px;
	font-family: var(--font-ropa);

	h2 {
		font-size: var(--fz-xxl);
		letter-spacing: -0.09em;
		text-align: right;
		color: var(--orange)
	}

	h4 {
		text-align: left;
		color: var(--turq);
		font-size: 1em;
		letter-spacing: -0.09em;
		text-align: right;
	}

	p {
		text-align: right;
		color: var(--turq);
		font-size: var(--fz-xl);
		letter-spacing: -0.04em
	}


	.hithere {
		grid-column: 1/2;
		width: 50vw;
		display: flex;
		flex-direction: column;
		justify-content: center;
	}

	.animation {
		grid-column: 2/3;
		width: 40vw;
		display: flex;
		align-items: center;
		justify-content: center;
	}

	.svg {
		width: 30px;
	}

	email-link {
		grid-column: 1/2;
		margin-top: 30px;
		color: var(--orange);
		background-color: transparent;
		border: 1px solid var(--orange);
		border-radius: var(--border-radius);
		padding: 1.25rem 1.75rem;
		font-size: var(--fz-sm);
		font-family: var(--font-sans);
		line-height: 1;
		text-decoration: none;
		cursor: pointer;
		transition: var(--transition);
		&:hover,
		&:focus,
		&:active {
		background-color: var(--turq);
		}
`;

function Hero() {
  return (
    <StyledHeroSection>
      <div className="hithere">
        <h4>Hi, my name is</h4>
        <h2>Liam Tyrie</h2>
        <p>I design and develop bespoke websites and web applications.</p>
      </div>

      <div className="animation">
        <Delay wait={1000}>
          <Animations />
        </Delay>
      </div>
    </StyledHeroSection>
  );
}

export default Hero;
